import React from "react";
import barba, {gsap} from '@barba/core';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from "react-router-dom";

function Home() {
  barba.init({
    transitions: [{
      name: 'opacity-transition',
      leave(data) {
        return gsap.to(data.current.container, {
          opacity: 0
        });
      },
      enter(data) {
        return gsap.from(data.next.container, {
          opacity: 0
        });
      }
    }]
  });
  return (
    <>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/barba.js/1.0.0/barba.min.js" integrity="sha512-7b1FfB2MGnB65aK6jrbS8k3REB+8bEE8UfP/TEwacejD0g02nLLI1VL4IPcKyiLk2lf7HJWaUYEUWc65aqaXQg==" crossorigin="anonymous"></script>
    <div className = "homepage">
      <div className="intro">
        <h1 className = "firstPageDivs">Pierce Butler</h1>
        <h1 className = "firstPageDivs">Software Engineer</h1>
        <h1 className = "firstPageDivs">Front-end Enthusiast</h1>
        </div>
        <div id="barba-wrapper">
          <div className = "barba-container"></div>
      <div className="links">
        <Link to = "/aboutme" className = "firstPageDivs">About Me</Link>
        <Link to = "/portfolio" className = "firstPageDivs">Portfolio</Link>
        <Link to = "/contact" className = "firstPageDivs">Contact me</Link>
        </div>
        </div>
      </div>
      </>
  );
}

export default Home;
