import React from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
const container = document.getElementsByClassName('container')
const colors = ["#ff9a00", "#ff165d", "#6fe7dd", "#ff5200", "#2ecc71"]
const squares = 300


function Home() {
  for (let i = 0; i < squares; i++){
    const square = document.createElement('div')
    square.classList.add('square')

    square.addEventListener('mouseover', () => {
      setColor(square)
    })
    square.addEventListener('mouseout', () => {
      removeColor(square)
    })

    container.appendChild(square)
  }

  function setColor(element) {
    const color = getRandomColor()
    element.style.background = color
  }

  function removeColor(element) {
    element.style.background = "#7a3d"
  }
  return (
    <>
      <div className="container"></div>
      <div className="homepage">
        <div className="intro">
          <h1 className="firstPageDivs">Pierce Butler</h1>
          <h1 className="firstPageDivs">Software Engineer</h1>
          <h1 className="firstPageDivs">Front-end Enthusiast</h1>
        </div>
        <div className="links">
          <Link to="/aboutme" className="firstPageDivs">
            About Me
          </Link>
          <Link to="/portfolio" className="firstPageDivs">
            Portfolio
          </Link>
          <Link to="/contact" className="firstPageDivs">
            Contact me
          </Link>
        </div>
      </div>
    </>
  );
}

export default Home;
